/**
 * 일반 사용자 대시보드 컴포넌트
 * - 계정 정보 표시 및 수정
 * - 사용량 정보 및 통계
 * - 구독 정보 확인
 */

import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import DarkModeToggle from "./DarkModeToggle";
import LoadingSpinner from "./LoadingSpinner";

const UserDashboard = () => {
  const { user, getAuthToken } = useAuth();
  const navigate = useNavigate();

  const [loading, setLoading] = useState(true);
  const [userProfile, setUserProfile] = useState(null);
  const [usageData, setUsageData] = useState(null);
  const [subscriptionData, setSubscriptionData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({ name: "" });
  const [usagePeriod, setUsagePeriod] = useState("month");

  useEffect(() => {
    loadDashboardData();
  }, [usagePeriod]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const token = await getAuthToken();

      // 병렬로 데이터 로드
      const [profileRes, usageRes, subscriptionRes] = await Promise.all([
        fetch(`${process.env.REACT_APP_API_URL}/user/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        fetch(
          `${process.env.REACT_APP_API_URL}/user/usage?period=${usagePeriod}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        ),
        fetch(`${process.env.REACT_APP_API_URL}/user/subscription`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      const profile = await profileRes.json();
      const usage = await usageRes.json();
      const subscription = await subscriptionRes.json();

      setUserProfile(profile);
      setUsageData(usage);
      setSubscriptionData(subscription);
      setEditForm({ name: profile.name || "" });
    } catch (error) {
      console.error("대시보드 데이터 로드 오류:", error);
      toast.error("데이터를 불러오는데 실패했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const handleProfileUpdate = async () => {
    try {
      const token = await getAuthToken();

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/user/profile`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(editForm),
        }
      );

      if (response.ok) {
        toast.success("프로필이 업데이트되었습니다.");
        setIsEditing(false);
        loadDashboardData();
      } else {
        toast.error("프로필 업데이트에 실패했습니다.");
      }
    } catch (error) {
      console.error("프로필 업데이트 오류:", error);
      toast.error("프로필 업데이트에 실패했습니다.");
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "-";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const formatNumber = (number) => {
    if (typeof number !== "number") return "0";
    return number.toLocaleString();
  };

  const getUsageBarWidth = (used, limit) => {
    if (!limit) return 0;
    return Math.min((used / limit) * 100, 100);
  };

  const getUsageColor = (percentage) => {
    if (percentage >= 90) return "bg-red-500";
    if (percentage >= 70) return "bg-yellow-500";
    return "bg-blue-500";
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-dark-primary flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-primary overflow-y-auto">
      {/* 헤더 */}
      <div className="bg-white dark:bg-dark-secondary shadow-sm border-b border-gray-200 dark:border-dark-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center py-4 relative">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate("/chat")}
                className="text-blue-600 hover:text-blue-700 dark:text-blue-400 dark:hover:text-blue-300 font-medium"
              >
                ← 채팅으로 돌아가기
              </button>
            </div>
            <div className="absolute right-4 sm:right-6 lg:right-8 flex items-center space-x-4">
              <span className="text-sm text-gray-600 dark:text-gray-300">
                {user?.name || user?.email}
              </span>
              <DarkModeToggle />
            </div>
          </div>
        </div>
      </div>

      {/* 메인 컨텐츠 */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            대시보드
          </h1>
          <p className="mt-2 text-gray-600 dark:text-gray-300">
            계정 정보와 사용량을 확인하고 관리하세요.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* 계정 정보 */}
          <div className="lg:col-span-1">
            <div className="bg-white dark:bg-dark-secondary rounded-lg shadow-sm border border-gray-200 dark:border-dark-border">
              <div className="px-6 py-4 border-b border-gray-200 dark:border-dark-border">
                <div className="flex justify-between items-center">
                  <h2 className="text-lg font-semibold text-gray-900 dark:text-white">
                    계정 정보
                  </h2>
                  <button
                    onClick={() => setIsEditing(!isEditing)}
                    className="text-sm text-blue-600 hover:text-blue-700 dark:text-blue-400 dark:hover:text-blue-300"
                  >
                    {isEditing ? "취소" : "수정"}
                  </button>
                </div>
              </div>
              <div className="px-6 py-4 space-y-4">
                {isEditing ? (
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                        이름
                      </label>
                      <input
                        type="text"
                        value={editForm.name}
                        onChange={(e) =>
                          setEditForm({ ...editForm, name: e.target.value })
                        }
                        className="w-full px-3 py-2 border border-gray-300 dark:border-dark-border rounded-md shadow-sm bg-white dark:bg-dark-tertiary text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400"
                      />
                    </div>
                    <div className="flex space-x-3">
                      <button
                        onClick={handleProfileUpdate}
                        className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
                      >
                        저장
                      </button>
                      <button
                        onClick={() => setIsEditing(false)}
                        className="flex-1 bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400 transition-colors"
                      >
                        취소
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        이메일
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        {userProfile?.email || "-"}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        이름
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        {userProfile?.name || "-"}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        계정 타입
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200">
                          일반 사용자
                        </span>
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        가입일
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        {formatDate(userProfile?.created_at)}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* 사용량 정보 */}
          <div className="lg:col-span-2">
            <div className="bg-white dark:bg-dark-secondary rounded-lg shadow-sm border border-gray-200 dark:border-dark-border">
              <div className="px-6 py-4 border-b border-gray-200 dark:border-dark-border">
                <div className="flex justify-between items-center">
                  <h2 className="text-lg font-semibold text-gray-900 dark:text-white">
                    사용량 정보
                  </h2>
                  <select
                    value={usagePeriod}
                    onChange={(e) => setUsagePeriod(e.target.value)}
                    className="text-sm border border-gray-300 dark:border-dark-border rounded-md px-2 py-1 bg-white dark:bg-dark-tertiary text-gray-900 dark:text-white"
                  >
                    <option value="day">최근 1일</option>
                    <option value="week">최근 1주</option>
                    <option value="month">최근 1개월</option>
                  </select>
                </div>
              </div>
              <div className="px-6 py-4">
                {usageData ? (
                  <div className="space-y-6">
                    {/* 토큰 사용량 */}
                    <div>
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                          토큰 사용량
                        </span>
                        <span className="text-sm text-gray-500 dark:text-gray-400">
                          {formatNumber(usageData.total_tokens)} /{" "}
                          {formatNumber(usageData.token_limit)}
                        </span>
                      </div>
                      <div className="w-full bg-gray-200 dark:bg-dark-tertiary rounded-full h-2">
                        <div
                          className={`h-2 rounded-full transition-all duration-300 ${getUsageColor(
                            usageData.usage_percentage
                          )}`}
                          style={{
                            width: `${getUsageBarWidth(
                              usageData.total_tokens,
                              usageData.token_limit
                            )}%`,
                          }}
                        ></div>
                      </div>
                      <div className="mt-1 text-xs text-gray-500 dark:text-gray-400">
                        {usageData.usage_percentage.toFixed(1)}% 사용
                      </div>
                    </div>

                    {/* 요청 횟수 */}
                    <div>
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                          요청 횟수
                        </span>
                        <span className="text-sm text-gray-500 dark:text-gray-400">
                          {formatNumber(usageData.total_requests)}회
                        </span>
                      </div>
                    </div>

                    {/* 일별 사용량 차트 (간단한 형태) */}
                    {Object.keys(usageData.daily_usage || {}).length > 0 && (
                      <div>
                        <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                          일별 사용량
                        </h3>
                        <div className="space-y-2 max-h-48 overflow-y-auto">
                          {Object.entries(usageData.daily_usage)
                            .sort(([a], [b]) => b.localeCompare(a))
                            .map(([date, data]) => (
                              <div
                                key={date}
                                className="flex justify-between items-center text-sm"
                              >
                                <span className="text-gray-600 dark:text-gray-400">
                                  {formatDate(date)}
                                </span>
                                <div className="text-right">
                                  <div className="text-gray-900 dark:text-white">
                                    {formatNumber(data.tokens)} 토큰
                                  </div>
                                  <div className="text-gray-500 dark:text-gray-400">
                                    {formatNumber(data.requests)}회
                                  </div>
                                </div>
                              </div>
                            ))}
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                    사용량 데이터를 불러올 수 없습니다.
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* 구독 정보 */}
          <div className="lg:col-span-3">
            <div className="bg-white dark:bg-dark-secondary rounded-lg shadow-sm border border-gray-200 dark:border-dark-border">
              <div className="px-6 py-4 border-b border-gray-200 dark:border-dark-border">
                <h2 className="text-lg font-semibold text-gray-900 dark:text-white">
                  구독 정보
                </h2>
              </div>
              <div className="px-6 py-4">
                {subscriptionData ? (
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        플랜
                      </div>
                      <div className="text-gray-900 dark:text-white capitalize">
                        <span
                          className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            subscriptionData.plan_type === "free"
                              ? "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200"
                              : "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                          }`}
                        >
                          {subscriptionData.plan_type === "free"
                            ? "무료 플랜"
                            : "프리미엄 플랜"}
                        </span>
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        상태
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        <span
                          className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            subscriptionData.status === "active"
                              ? "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                              : "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
                          }`}
                        >
                          {subscriptionData.status === "active"
                            ? "활성"
                            : "비활성"}
                        </span>
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        시작일
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        {formatDate(subscriptionData.start_date)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-500 dark:text-gray-400">
                        만료일
                      </div>
                      <div className="text-gray-900 dark:text-white">
                        {formatDate(subscriptionData.expiry_date)}
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                    구독 정보를 불러올 수 없습니다.
                  </div>
                )}
              </div>
            </div>
