import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  CloudArrowUpIcon,
  XMarkIcon,
  Bars3Icon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import {
  promptCardAPI,
  PROMPT_CARD_CATEGORIES,
  AVAILABLE_MODELS,
  handleAPIError,
} from "../services/api";
import ChatInterface from "./ChatInterface";

// 고정된 프롬프트 카테고리 템플릿 (순서 포함)
const FIXED_PROMPT_CATEGORIES = [
  {
    id: "역할",
    name: "역할",
    order: 1,
    color: "bg-blue-100 text-blue-800",
    description: "AI의 역할과 정체성을 정의합니다",
    defaultContent:
      "당신은 전문적인 기사 제목 작성자입니다. 서울경제신문의 스타일에 맞는 매력적이고 정확한 제목을 작성해주세요.",
  },
  {
    id: "가이드라인",
    name: "가이드라인",
    order: 2,
    color: "bg-purple-100 text-purple-800",
    description: "작업 수행을 위한 지침과 규칙을 제공합니다",
    defaultContent:
      "제목 작성 시 다음 가이드라인을 따라주세요:\n- 명확하고 간결하게 작성\n- 독자의 관심을 끌 수 있는 표현 사용\n- 기사의 핵심 내용을 정확히 반영",
  },
  {
    id: "워크플로우",
    name: "워크플로우",
    order: 3,
    color: "bg-green-100 text-green-800",
    description: "작업 진행 단계와 프로세스를 안내합니다",
    defaultContent:
      "다음 단계를 따라 제목을 생성해주세요:\n1. 기사 내용 분석\n2. 핵심 키워드 추출\n3. 여러 제목 후보 생성\n4. 최적의 제목 선택",
  },
  {
    id: "출력결과",
    name: "출력결과",
    order: 4,
    color: "bg-orange-100 text-orange-800",
    description: "결과물의 형식과 구조를 정의합니다",
    defaultContent:
      "결과는 다음 형식으로 제공해주세요:\n- 메인 제목 1개\n- 대안 제목 2-3개\n- 각 제목의 선택 이유 간단 설명",
  },
  {
    id: "예시",
    name: "예시",
    order: 5,
    color: "bg-yellow-100 text-yellow-800",
    description: "참고할 수 있는 예시와 샘플을 제공합니다",
    defaultContent:
      "좋은 제목 예시:\n- '반도체 수출 급증, 3분기 실적 기대감 ↑'\n- '부동산 규제 완화로 거래량 30% 증가'\n- 'K-뷰티 해외진출 가속화, 올해 수출 목표 달성'",
  },
  {
    id: "검수",
    name: "검수",
    order: 6,
    color: "bg-red-100 text-red-800",
    description: "품질 검증과 최종 검토 기준을 제시합니다",
    defaultContent:
      "생성된 제목을 다음 기준으로 검수해주세요:\n- 문법적 정확성\n- 사실 관계 확인\n- 브랜드 가이드라인 준수\n- 독자 친화성",
  },
];

// 프롬프트 폼 (편집 전용)
const PromptEditForm = ({ onSubmit, onCancel, initialData }) => {
  const [formData, setFormData] = useState({
    title: "",
    category: "역할",
    model: "claude-3-5-sonnet-20241022",
    temperature: 0.7,
    content: "",
    enabled: true,
    ...initialData,
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.content.trim()) {
      toast.error("프롬프트 내용을 입력해주세요.");
      return;
    }

    try {
      await onSubmit(formData);
    } catch (error) {
      console.error("폼 제출 실패:", error);
      toast.error("폼 제출에 실패했습니다.");
    }
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const categoryInfo = FIXED_PROMPT_CATEGORIES.find(
    (cat) => cat.id === formData.category
  );

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-8">
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            프롬프트 편집
          </h2>
          <p className="text-gray-600">{categoryInfo?.description}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* 카테고리 정보 (읽기 전용) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              카테고리 (수정 불가)
            </label>
            <div className="flex items-center space-x-3">
              <span
                className={`inline-flex items-center px-3 py-1 rounded-md text-sm font-medium ${categoryInfo?.color}`}
              >
                {categoryInfo?.order}. {categoryInfo?.name}
              </span>
              <span className="text-sm text-gray-500">
                {categoryInfo?.description}
              </span>
            </div>
          </div>

          {/* 제목 (읽기 전용) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              제목 (수정 불가)
            </label>
            <input
              type="text"
              value={formData.title}
              readOnly
              className="w-full px-3 py-2 border border-gray-300 rounded-lg bg-gray-50 text-gray-500"
            />
          </div>

          {/* 모델과 온도 설정 */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                모델
              </label>
              <select
                value={formData.model}
                onChange={(e) => handleChange("model", e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="claude-3-5-sonnet-20241022">
                  Claude 3.5 Sonnet
                </option>
                <option value="claude-3-sonnet-20240229">
                  Claude 3 Sonnet
                </option>
                <option value="claude-3-haiku-20240307">Claude 3 Haiku</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                온도: {formData.temperature}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={formData.temperature}
                onChange={(e) =>
                  handleChange("temperature", parseFloat(e.target.value))
                }
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
              />
              <div className="flex justify-between text-xs text-gray-500 mt-1">
                <span>정확함 (0.0)</span>
                <span>창의적 (1.0)</span>
              </div>
            </div>
          </div>

          {/* 프롬프트 내용 편집 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              프롬프트 내용
            </label>
            <textarea
              value={formData.content}
              onChange={(e) => handleChange("content", e.target.value)}
              rows={12}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프롬프트 내용을 입력하세요..."
            />
            <div className="mt-2 text-right">
              <button
                type="button"
                onClick={() =>
                  handleChange("content", categoryInfo?.defaultContent || "")
                }
                className="text-xs text-blue-600 hover:text-blue-800"
              >
                기본 내용으로 복원
              </button>
            </div>
          </div>

          {/* 버튼 */}
          <div className="flex justify-end space-x-3 pt-6">
            <button
              type="button"
              onClick={onCancel}
              className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              취소
            </button>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
            >
              저장
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// 개선된 사이드바 프롬프트 카드 (편집 전용)
const SidebarPromptCard = ({ card, onEdit, onToggle }) => {
  const [isToggling, setIsToggling] = useState(false);

  const categoryInfo = FIXED_PROMPT_CATEGORIES.find(
    (cat) => cat.id === card.category
  );

  const getModelDisplayName = (modelId) => {
    if (modelId?.includes("claude-3-5-sonnet")) return "Claude 3.5 Sonnet";
    if (modelId?.includes("claude-3-sonnet")) return "Claude 3 Sonnet";
    if (modelId?.includes("claude")) return "Claude";
    return "Unknown Model";
  };

  // 즉시 반응하는 토글 (낙관적 업데이트)
  const handleToggleClick = async () => {
    if (isToggling) return;

    setIsToggling(true);
    const newEnabled = !card.enabled;

    // 즉시 UI 업데이트 (낙관적 업데이트)
    card.enabled = newEnabled;

    try {
      await onToggle(card.promptId || card.id, newEnabled);
    } catch (error) {
      // 실패시 원래 상태로 복원
      card.enabled = !newEnabled;
      toast.error("상태 변경에 실패했습니다.");
    } finally {
      setIsToggling(false);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  return (
    <div className="group p-4 rounded-xl border border-gray-200 bg-white hover:bg-gray-50 transition-all duration-200 relative">
      <div className="flex items-start justify-between">
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-2">
            <span
              className={`inline-flex items-center px-2 py-1 rounded-md text-xs font-medium ${categoryInfo?.color}`}
            >
              {categoryInfo?.order}. {categoryInfo?.name}
            </span>
            <h6 className="text-sm font-medium text-gray-900 truncate">
              {card.title}
            </h6>
          </div>
          <div className="text-xs text-gray-500 space-y-1">
            <div>Temperature: {card.temperature || 0.7}</div>
            <div>Model: {getModelDisplayName(card.model)}</div>
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          {/* 편집 버튼 */}
          <button
            onClick={handleEdit}
            className="p-1 text-gray-400 hover:text-blue-600 rounded transition-all hover:bg-blue-50"
            title="편집"
          >
            <PencilIcon className="h-4 w-4" />
          </button>

          {/* 토글 스위치 */}
          <button
            onClick={handleToggleClick}
            disabled={isToggling}
            className={`w-8 h-4 rounded-full relative transition-colors ${
              card.enabled ? "bg-blue-600" : "bg-gray-300"
            } ${isToggling ? "opacity-50" : ""}`}
            title={card.enabled ? "비활성화" : "활성화"}
          >
            <div
              className={`w-3 h-3 bg-white rounded-full shadow transform transition-transform absolute top-0.5 ${
                card.enabled ? "translate-x-4" : "translate-x-0.5"
              }`}
            ></div>
          </button>
        </div>
      </div>
    </div>
  );
};

// 메인 PromptCardManager 컴포넌트 (템플릿 시스템)
const PromptCardManager = ({ projectId, onCardsChanged, projectName }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingCard, setEditingCard] = useState(null);

  // 프롬프트 카드 목록 로딩 및 기본 카드 생성
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(
        projectId,
        true,
        true
      );
      let cards = response.promptCards || [];

      // 기본 카테고리별로 카드가 없으면 생성 (임시 비활성화)
      console.log('Loaded cards from API:', cards.length);
      // TODO: 기본 카드 생성 로직을 API 스키마 확인 후 다시 활성화
      /*
      for (const category of FIXED_PROMPT_CATEGORIES) {
        const existingCard = cards.find(
          (card) => card.category === category.id
        );
        if (!existingCard) {
          // 기본 카드 생성
          const defaultCard = {
            title: `${category.name} 프롬프트`,
            category: category.id,
            model_name: "claude-3-5-sonnet-20241022",
            temperature: 0.7,
            prompt_text: category.defaultContent,
            enabled: true,
            step_order: category.order || 1,
          };

          try {
            console.log(`Creating default card for ${category.name}:`, defaultCard);
            const newCard = await promptCardAPI.createPromptCard(
              projectId,
              defaultCard
            );
            cards.push(newCard);
          } catch (error) {
            console.error(`기본 카드 생성 실패 (${category.name}):`, error);
            if (error.response) {
              console.error('API Response:', error.response.data);
              console.error('Status:', error.response.status);
            }
          }
        }
      }
      */

      // 순서대로 정렬
      cards.sort((a, b) => {
        const aCategory = FIXED_PROMPT_CATEGORIES.find(
          (cat) => cat.id === a.category
        );
        const bCategory = FIXED_PROMPT_CATEGORIES.find(
          (cat) => cat.id === b.category
        );
        return (aCategory?.order || 999) - (bCategory?.order || 999);
      });

      setPromptCards(cards);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 프롬프트 카드 수정
  const handleUpdateCard = async (cardData) => {
    try {
      await promptCardAPI.updatePromptCard(
        projectId,
        editingCard.promptId || editingCard.id,
        cardData
      );
      toast.success("프롬프트 카드가 수정되었습니다!");
      setEditingCard(null);
      setShowForm(false);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 수정 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 활성/비활성 토글 (백그라운드 처리)
  const handleToggleCard = async (promptId, enabled) => {
    try {
      await promptCardAPI.togglePromptCard(projectId, promptId, enabled);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      throw error; // SidebarPromptCard에서 처리
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  // 카테고리별 그룹화 (순서 유지) - 빈 카테고리도 표시
  const groupedCards = FIXED_PROMPT_CATEGORIES.reduce((groups, category) => {
    const categoryCards = promptCards.filter(
      (card) => card.category === category.id
    );
    groups[category.id] = {
      ...category,
      cards: categoryCards,
    };
    return groups;
  }, {});

  // 디버깅용 로그
  console.log('PromptCardManager Debug:');
  console.log('promptCards:', promptCards);
  console.log('groupedCards:', groupedCards);

  const activeCards = promptCards.filter(
    (card) => card.enabled !== false
  ).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 왼쪽 채팅 영역 */}
      <div className="flex-1 flex flex-col">
        {/* 메인 콘텐츠 영역 */}
        <div className="flex-1 overflow-hidden">
          {showForm ? (
            <div className="h-full overflow-y-auto p-6">
              <PromptEditForm
                onSubmit={handleUpdateCard}
                onCancel={handleCancelForm}
                initialData={editingCard}
              />
            </div>
          ) : (
            <ChatInterface projectId={projectId} projectName={projectName} />
          )}
        </div>
      </div>

      {/* 우측 사이드바 */}
      <aside className="w-96 bg-white border-l border-gray-200 shadow-sm flex flex-col">
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
          <div className="flex items-center space-x-2">
            <Cog6ToothIcon className="h-5 w-5 text-gray-600" />
            <h3 className="text-sm font-semibold text-gray-900">
              프롬프트 템플릿
            </h3>
          </div>
          <div className="text-xs text-gray-500">
            {activeCards}/{promptCards.length} 활성
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4">
          <div className="space-y-6">
            {loading ? (
              <div className="text-center py-8">
                <DocumentTextIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">프롬프트 카드를 로딩 중...</p>
              </div>
            ) : (
              Object.values(groupedCards).map((categoryGroup) => (
                <div key={categoryGroup.id}>
                  <div className="flex items-center justify-between mb-2">
                    <h5 className="text-xs font-medium text-gray-700 uppercase tracking-wider">
                      {categoryGroup.order}. {categoryGroup.name}
                    </h5>
                    <span className="text-xs text-gray-500">
                      {categoryGroup.description}
                    </span>
                  </div>
                  <div className="space-y-2">
                    {categoryGroup.cards.length > 0 ? (
                      categoryGroup.cards.map((card) => (
                        <SidebarPromptCard
                          key={card.promptId || card.id}
                          card={card}
                          onEdit={handleEditCard}
                          onToggle={handleToggleCard}
                        />
                      ))
                    ) : (
                      <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center">
                        <p className="text-xs text-gray-500 mb-2">
                          {categoryGroup.name} 프롬프트가 없습니다
                        </p>
                        <button 
                          onClick={() => {
                            // 해당 카테고리의 기본 카드 생성
                            const defaultCard = {
                              title: `${categoryGroup.name} 프롬프트`,
                              category: categoryGroup.id,
                              model_name: "claude-3-5-sonnet-20241022",
                              temperature: 0.7,
                              prompt_text: categoryGroup.defaultContent,
                              enabled: true,
                              step_order: categoryGroup.order || 1,
                            };
                            setEditingCard(defaultCard);
                            setShowForm(true);
                          }}
                          className="text-xs text-blue-600 hover:text-blue-800"
                        >
                          + 카드 추가
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* 사이드바 하단 - 도움말 */}
        <div className="p-4 border-t border-gray-200 bg-gray-50">
          <div className="text-xs text-gray-600">
            <p className="font-medium mb-1">💡 템플릿 시스템</p>
            <p>각 카테고리별 프롬프트를 편집하여 AI 응답을 개선하세요.</p>
          </div>
        </div>
      </aside>
    </div>
  );
};

export default PromptCardManager;
