import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import { 
  PaperAirplaneIcon, 
  ClipboardDocumentIcon,
  CheckCircleIcon,
  UserIcon,
  CpuChipIcon 
} from '@heroicons/react/24/outline';
import { toast } from 'react-hot-toast';
import { generateAPI } from '../services/api';

const ChatInterface = ({ projectId, projectName }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [copiedMessage, setCopiedMessage] = useState(null);
  const [streamingMessage, setStreamingMessage] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const streamingRef = useRef(null);

  // 대화 세션 초기화
  useEffect(() => {
    const welcomeMessage = {
      id: Date.now(),
      type: 'ai',
      content: `안녕하세요! 저는 ${projectName}의 AI 제목 작가입니다. 🎯\n\n기사 내용을 입력해주시면 다양한 스타일의 제목을 제안해드릴게요. 제목을 수정하거나 다른 스타일로 바꾸고 싶으시면 언제든 말씀해주세요!`,
      timestamp: new Date()
    };
    setMessages([welcomeMessage]);
  }, [projectName]);

  // 메시지 추가 시 스크롤 하단으로
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // 스트리밍 타이핑 효과
  const simulateStreaming = (text, onComplete) => {
    setIsStreaming(true);
    setStreamingMessage('');

    let index = 0;
    const interval = setInterval(() => {
      if (index < text.length) {
        setStreamingMessage(prev => prev + text[index]);
        index++;
        scrollToBottom();
      } else {
        clearInterval(interval);
        setIsStreaming(false);
        setStreamingMessage('');
        onComplete();
      }
    }, 30); // 30ms 간격으로 한 글자씩 출력

    streamingRef.current = interval;
  };

  // 메시지 전송
  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    try {
      // 대화 컨텍스트 생성 (최근 5개 메시지)
      const recentMessages = messages.slice(-5);
      const conversationContext = recentMessages
        .map(msg => `${msg.type === 'user' ? 'Human' : 'Assistant'}: ${msg.content}`)
        .join('\n\n');

      const contextualInput = recentMessages.length > 0 
        ? `[이전 대화 컨텍스트]\n${conversationContext}\n\n[새로운 요청]\nHuman: ${currentInput}`
        : currentInput;

      const result = await generateAPI.generateTitle(
        projectId,
        contextualInput,
        null // onProgress callback
      );

      // 스트리밍 효과로 AI 응답 출력
      simulateStreaming(result.result, () => {
        const aiMessage = {
          id: Date.now() + 1,
          type: 'ai',
          content: result.result,
          timestamp: new Date(),
          debug: result.debug
        };
        setMessages(prev => [...prev, aiMessage]);
      });

    } catch (error) {
      console.error('메시지 전송 실패:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: '죄송합니다. 메시지 처리 중 오류가 발생했습니다. 다시 시도해주세요.',
        timestamp: new Date(),
        isError: true
      };
      setMessages(prev => [...prev, errorMessage]);
      toast.error('메시지 전송에 실패했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  // Enter 키로 전송
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // 텍스트 복사
  const copyToClipboard = (text, messageId) => {
    // 제목만 추출 (• 로 시작하는 라인들)
    const titles = text.match(/• ([^\n]+)/g);
    const textToCopy = titles ? titles.map(t => t.replace('• ', '')).join('\n') : text;

    navigator.clipboard.writeText(textToCopy)
      .then(() => {
        setCopiedMessage(messageId);
        toast.success('클립보드에 복사되었습니다!');
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error('복사에 실패했습니다.');
      });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-sm border border-gray-200">
      {/* 채팅 헤더 */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50 rounded-t-lg">
        <div className="flex items-center">
          <CpuChipIcon className="h-6 w-6 text-blue-600 mr-2" />
          <h3 className="text-lg font-semibold text-gray-900">AI 제목 작가</h3>
          <span className="ml-2 text-sm text-gray-500">• {projectName}</span>
        </div>
        <button
          onClick={() => setMessages(messages.slice(0, 1))} // 첫 번째 환영 메시지만 남기기
          className="text-sm text-gray-500 hover:text-gray-700 px-3 py-1 rounded-md hover:bg-gray-200 transition-colors"
        >
          대화 초기화
        </button>
      </div>

      {/* 메시지 영역 */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 min-h-0">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div className={`flex max-w-[80%] ${message.type === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
              {/* 아바타 */}
              <div className={`flex-shrink-0 ${message.type === 'user' ? 'ml-3' : 'mr-3'}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  message.type === 'user' 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-200 text-gray-600'
                }`}>
                  {message.type === 'user' ? (
                    <UserIcon className="w-5 h-5" />
                  ) : (
                    <CpuChipIcon className="w-5 h-5" />
                  )}
                </div>
              </div>

              {/* 메시지 버블 */}
              <div className={`rounded-lg px-4 py-3 ${
                message.type === 'user'
                  ? 'bg-blue-600 text-white'
                  : message.isError
                  ? 'bg-red-50 text-red-900 border border-red-200'
                  : 'bg-gray-100 text-gray-900'
              }`}>
                <div className="whitespace-pre-wrap text-sm leading-relaxed">
                  {message.content}
                </div>

                {/* AI 메시지에 복사 버튼 추가 */}
                {message.type === 'ai' && !message.isError && (
                  <div className="flex items-center justify-between mt-3 pt-2 border-t border-gray-200">
                    <span className="text-xs text-gray-500">
                      {message.timestamp.toLocaleTimeString('ko-KR', {
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                    <button
                      onClick={() => copyToClipboard(message.content, message.id)}
                      className="flex items-center text-xs text-gray-500 hover:text-gray-700 px-2 py-1 rounded hover:bg-gray-200 transition-colors"
                    >
                      {copiedMessage === message.id ? (
                        <>
                          <CheckCircleIcon className="w-3 h-3 mr-1 text-green-600" />
                          복사됨
                        </>
                      ) : (
                        <>
                          <ClipboardDocumentIcon className="w-3 h-3 mr-1" />
                          복사
                        </>
                      )}
                    </button>
                  </div>
                )}

                {/* 디버그 정보 (개발 모드) */}
                {message.debug && (
                  <details className="mt-2 text-xs">
                    <summary className="text-gray-500 cursor-pointer hover:text-gray-700">
                      🔍 디버그 정보
                    </summary>
                    <div className="mt-1 p-2 bg-white rounded border text-gray-600 space-y-1">
                      <div>프롬프트 수: {message.debug.promptCount || 0}개</div>
                      <div>기본 가이드라인: {message.debug.usedDefaultGuidelines ? '사용' : '미사용'}</div>
                      {message.debug.promptTitles && (
                        <div>적용된 프롬프트: {message.debug.promptTitles.join(', ')}</div>
                      )}
                    </div>
                  </details>
                )}
              </div>
            </div>
          </div>
        ))}

        {/* 로딩 인디케이터 */}
        {isLoading && (
          <div className="flex justify-start">
            <div className="flex mr-3">
              <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center">
                <CpuChipIcon className="w-5 h-5 text-gray-600" />
              </div>
            </div>
            <div className="bg-gray-100 rounded-lg px-4 py-3">
              <div className="flex items-center space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* 입력 영역 */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex items-end space-x-3">
          <div className="flex-1">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="기사 내용을 입력하거나 제목 수정 요청을 해주세요..."
              className="w-full p-3 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              rows="3"
              disabled={isLoading}
            />
          </div>
          <button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
          >
            <PaperAirplaneIcon className="w-5 h-5" />
          </button>
        </div>

        <div className="flex items-center justify-between mt-2 text-xs text-gray-500">
          <span>Shift + Enter로 줄바꿈, Enter로 전송</span>
          <span>{inputMessage.length}/2000</span>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
