import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  CloudArrowUpIcon,
  XMarkIcon,
  Bars3Icon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import {
  promptCardAPI,
  PROMPT_CARD_CATEGORIES,
  AVAILABLE_MODELS,
  handleAPIError,
} from "../services/api";
import ChatInterface from "./ChatInterface";

// 고정된 프롬프트 카테고리 템플릿 (순서 포함)
const FIXED_PROMPT_CATEGORIES = [
  {
    id: "instruction",
    name: "지시사항",
    order: 1,
    color: "bg-blue-100 text-blue-800",
    description: "AI의 역할과 작업 지시사항을 정의합니다",
    defaultContent:
      "당신은 전문적인 기사 제목 작성자입니다. 서울경제신문의 스타일에 맞는 매력적이고 정확한 제목을 작성해주세요.",
  },
  {
    id: "knowledge",
    name: "지식 베이스",
    order: 2,
    color: "bg-purple-100 text-purple-800",
    description: "작업에 필요한 배경 지식과 정보를 제공합니다",
    defaultContent:
      "제목 작성 시 다음 가이드라인을 따라주세요:\n- 명확하고 간결하게 작성\n- 독자의 관심을 끌 수 있는 표현 사용\n- 기사의 핵심 내용을 정확히 반영",
  },
  {
    id: "secondary",
    name: "보조 지침",
    order: 3,
    color: "bg-green-100 text-green-800",
    description: "추가적인 작업 지침과 세부 규칙을 안내합니다",
    defaultContent:
      "다음 단계를 따라 제목을 생성해주세요:\n1. 기사 내용 분석\n2. 핵심 키워드 추출\n3. 여러 제목 후보 생성\n4. 최적의 제목 선택",
  },
  {
    id: "style_guide",
    name: "스타일 가이드",
    order: 4,
    color: "bg-orange-100 text-orange-800",
    description: "출력 형식과 스타일 규칙을 정의합니다",
    defaultContent:
      "결과는 다음 형식으로 제공해주세요:\n- 메인 제목 1개\n- 대안 제목 2-3개\n- 각 제목의 선택 이유 간단 설명",
  },
  {
    id: "validation",
    name: "검증 기준",
    order: 5,
    color: "bg-yellow-100 text-yellow-800",
    description: "품질 검증과 최종 검토 기준을 제시합니다",
    defaultContent:
      "생성된 제목을 다음 기준으로 검수해주세요:\n- 문법적 정확성\n- 사실 관계 확인\n- 브랜드 가이드라인 준수\n- 독자 친화성",
  },
  {
    id: "enhancement",
    name: "최적화",
    order: 6,
    color: "bg-red-100 text-red-800",
    description: "결과 개선과 최적화 방법을 안내합니다",
    defaultContent:
