// 개선된 사이드바 프롬프트 카드 (편집 전용)
const SidebarPromptCard = ({ card, onEdit, onToggle, onDelete }) => {
  const [isToggling, setIsToggling] = useState(false);

  const categoryInfo = FIXED_PROMPT_CATEGORIES.find(
    (cat) => cat.id === card.category
  );

  const getModelDisplayName = (modelId) => {
    if (modelId?.includes("claude-3-5-sonnet")) return "Claude 3.5 Sonnet";
    if (modelId?.includes("claude-3-sonnet")) return "Claude 3 Sonnet";
    if (modelId?.includes("claude")) return "Claude";
    return "Unknown Model";
  };

  // 즉시 반응하는 토글 (낙관적 업데이트)
  const handleToggleClick = async () => {
    if (isToggling) return;

    setIsToggling(true);
    const newEnabled = !card.enabled;

    // 즉시 UI 업데이트 (낙관적 업데이트)
    card.enabled = newEnabled;

    try {
      await onToggle(card.promptId || card.id, newEnabled);
    } catch (error) {
      // 실패시 원래 상태로 복원
      card.enabled = !newEnabled;
      toast.error("상태 변경에 실패했습니다.");
    } finally {
      setIsToggling(false);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  return (
    <div className="group p-4 rounded-xl border border-gray-200 bg-white hover:bg-gray-50 transition-all duration-200 relative">
      <div className="flex items-start justify-between">
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-2">
            <span
              className={`inline-flex items-center px-2 py-1 rounded-md text-xs font-medium ${categoryInfo?.color}`}
            >
              {categoryInfo?.order}. {categoryInfo?.name}
            </span>
            <h6 className="text-sm font-medium text-gray-900 truncate">
              {card.title}
            </h6>
          </div>
          <div className="text-xs text-gray-500 space-y-1">
            <div>Temperature: {card.temperature || 0.7}</div>
            <div>Model: {getModelDisplayName(card.model_name || card.model)}</div>
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          {/* 편집 버튼 */}
          <button
            onClick={handleEdit}
            className="p-1 text-gray-400 hover:text-blue-600 rounded transition-all hover:bg-blue-50"
            title="편집"
          >
            <PencilIcon className="h-4 w-4" />
          </button>

          {/* 삭제 버튼 */}
          <button
            onClick={() => {
              if (window.confirm('이 프롬프트 카드를 삭제하시겠습니까?')) {
                onDelete(card.promptId || card.id);
              }
            }}
            className="p-1 text-gray-400 hover:text-red-600 rounded transition-all hover:bg-red-50"
            title="삭제"
          >
            <TrashIcon className="h-4 w-4" />
          </button>

          {/* 개선된 토글 스위치 */}
          <button
            onClick={handleToggleClick}
            disabled={isToggling}
            className={`relative w-12 h-6 rounded-full transition-all duration-300 focus:outline-none focus:ring-3 focus:ring-offset-2 transform hover:scale-105 ${
              card.enabled 
                ? "bg-gradient-to-r from-emerald-400 via-emerald-500 to-emerald-600 shadow-lg shadow-emerald-200 focus:ring-emerald-300" 
                : "bg-gradient-to-r from-gray-200 to-gray-300 hover:from-gray-300 hover:to-gray-400 shadow-inner focus:ring-gray-300"
            } ${isToggling ? "opacity-70 cursor-not-allowed" : "cursor-pointer"}`}
            title={card.enabled ? "비활성화" : "활성화"}
          >
            <div
              className={`w-5 h-5 bg-white rounded-full shadow-lg transform transition-all duration-300 ease-in-out absolute top-0.5 flex items-center justify-center ${
                card.enabled ? "translate-x-6" : "translate-x-0.5"
              }`}
            >
              {/* 토글 내부 아이콘 */}
              <div className={`w-2 h-2 rounded-full transition-colors duration-200 ${
                card.enabled ? "bg-emerald-500" : "bg-gray-400"
              }`}></div>
            </div>
            {/* 활성화 상태 표시 */}
            {card.enabled && (
              <div className="absolute inset-0 rounded-full animate-pulse bg-emerald-400 opacity-20"></div>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// 메인 PromptCardManager 컴포넌트 (템플릿 시스템)
const PromptCardManager = ({ projectId, onCardsChanged, projectName }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingCard, setEditingCard] = useState(null);

  // 오케스트레이션 단계 상태
  const [currentStep, setCurrentStep] = useState(0);
  const [isExecuting, setIsExecuting] = useState(false);
  const [executionHistory, setExecutionHistory] = useState([]);

  // 프롬프트 카드 목록 로딩 및 기본 카드 생성
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(
        projectId,
        true,
        true
      );
      let cards = response.promptCards || [];

      // 기본 카테고리별로 카드가 없으면 생성 (임시 비활성화)
      console.log('Loaded cards from API:', cards.length);
      // TODO: 기본 카드 생성 로직을 API 스키마 확인 후 다시 활성화
      /*
      for (const category of FIXED_PROMPT_CATEGORIES) {
        const existingCard = cards.find(
          (card) => card.category === category.id
        );
        if (!existingCard) {
          // 기본 카드 생성
          const defaultCard = {
            title: `${category.name} 프롬프트`,
            category: category.id,
            model_name: "claude-3-5-sonnet-20241022",
            temperature: 0.7,
            prompt_text: category.defaultContent,
            enabled: true,
            step_order: category.order || 1,
          };

          try {
            console.log(`Creating default card for ${category.name}:`, defaultCard);
            const newCard = await promptCardAPI.createPromptCard(
              projectId,
              defaultCard
            );
            cards.push(newCard);
          } catch (error) {
            console.error(`기본 카드 생성 실패 (${category.name}):`, error);
            if (error.response) {
              console.error('API Response:', error.response.data);
              console.error('Status:', error.response.status);
            }
          }
        }
      }
      */

      // 순서대로 정렬
      cards.sort((a, b) => {
        const aCategory = FIXED_PROMPT_CATEGORIES.find(
          (cat) => cat.id === a.category
        );
        const bCategory = FIXED_PROMPT_CATEGORIES.find(
          (cat) => cat.id === b.category
        );
        return (aCategory?.order || 999) - (bCategory?.order || 999);
      });

      setPromptCards(cards);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 프롬프트 카드 수정/생성
  const handleUpdateCard = async (cardData) => {
    try {
      if (editingCard.promptId || editingCard.id) {
        // 기존 카드 수정
        await promptCardAPI.updatePromptCard(
          projectId,
          editingCard.promptId || editingCard.id,
          cardData
        );
        toast.success("프롬프트 카드가 수정되었습니다!");
      } else {
        // 새 카드 생성
        console.log('Creating new card:', cardData);
        await promptCardAPI.createPromptCard(projectId, cardData);
        toast.success("프롬프트 카드가 생성되었습니다!");
      }
      setEditingCard(null);
      setShowForm(false);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 ${editingCard.promptId || editingCard.id ? '수정' : '생성'} 실패: ${errorInfo.message}`);
      console.error('Card operation error:', error);
      if (error.response) {
        console.error('API Response:', error.response.data);
      }
    }
  };

  // 프롬프트 카드 활성/비활성 토글 (백그라운드 처리)
  const handleToggleCard = async (promptId, enabled) => {
    try {
      await promptCardAPI.togglePromptCard(projectId, promptId, enabled);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      throw error; // SidebarPromptCard에서 처리
    }
  };

  // 프롬프트 카드 삭제
  const handleDeleteCard = async (promptId) => {
    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트 카드가 삭제되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  // 카테고리별 그룹화 (순서 유지) - 빈 카테고리도 표시
  const groupedCards = FIXED_PROMPT_CATEGORIES.reduce((groups, category) => {
    const categoryCards = promptCards.filter(
      (card) => card.category === category.id
    );
    groups[category.id] = {
      ...category,
      cards: categoryCards,
    };
    return groups;
  }, {});

  // 디버깅용 로그
  console.log('PromptCardManager Debug:');
  console.log('promptCards:', promptCards);
  console.log('groupedCards:', groupedCards);

  const activeCards = promptCards.filter(
    (card) => card.enabled !== false
  ).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50 gap-4 p-4">
      {/* 왼쪽 채팅 영역 */}
      <div className="flex-1 flex flex-col">
        {/* 메인 콘텐츠 영역 */}
        <div className="flex-1 overflow-hidden">
          <ChatInterface projectId={projectId} projectName={projectName} />
        </div>
      </div>

      {/* 우측 사이드바 */}
      <aside className="w-96 bg-white border border-gray-200 rounded-lg shadow-sm flex flex-col">
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
          <div className="flex items-center space-x-2">
            <Cog6ToothIcon className="h-5 w-5 text-gray-600" />
            <h3 className="text-sm font-semibold text-gray-900">
              프롬프트 템플릿
            </h3>
          </div>
          <div className="text-xs text-gray-500">
            {activeCards}/{promptCards.length} 활성
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4">
          <div className="space-y-6">
            {loading ? (
              <div className="text-center py-8">
                <DocumentTextIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">프롬프트 카드를 로딩 중...</p>
              </div>
            ) : (
              Object.values(groupedCards).map((categoryGroup) => (
                <div key={categoryGroup.id}>
                  <div className="flex items-center justify-between mb-2">
                    <h5 className="text-xs font-medium text-gray-700 uppercase tracking-wider">
                      {categoryGroup.order}. {categoryGroup.name}
                    </h5>
                    <span className="text-xs text-gray-500">
                      {categoryGroup.description}
                    </span>
                  </div>
                  <div className="space-y-2">
                    {categoryGroup.cards.length > 0 ? (
                      categoryGroup.cards.map((card) => (
                        <SidebarPromptCard
                          key={card.promptId || card.id}
                          card={card}
                          onEdit={handleEditCard}
                          onToggle={handleToggleCard}
                          onDelete={handleDeleteCard}
                        />
                      ))
                    ) : (
                      <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center">
                        <p className="text-xs text-gray-500 mb-2">
                          {categoryGroup.name} 프롬프트가 없습니다
                        </p>
                        <button 
