import React, { useState } from "react";
import {
  SunIcon,
  MoonIcon,
  ComputerDesktopIcon,
  ChevronDownIcon,
} from "@heroicons/react/24/outline";
import { useTheme } from "../contexts/ThemeContext";

const DarkModeToggle = ({
  className = "",
  size = "md",
  showDropdown = false,
}) => {
  const { isDarkMode, themeMode, toggleDarkMode, setTheme } = useTheme();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const sizeClasses = {
    sm: "w-8 h-8",
    md: "w-10 h-10",
    lg: "w-12 h-12",
  };

  const iconSizeClasses = {
    sm: "h-4 w-4",
    md: "h-5 w-5",
    lg: "h-6 w-6",
  };

  const themeOptions = [
    {
      id: "light",
      label: "라이트 모드",
      icon: SunIcon,
      description: "밝은 테마",
    },
    {
      id: "dark",
      label: "다크 모드",
      icon: MoonIcon,
      description: "어두운 테마",
    },
    {
      id: "system",
      label: "시스템 설정",
      icon: ComputerDesktopIcon,
      description: "시스템 설정 따름",
    },
  ];

  const getCurrentIcon = () => {
    if (themeMode === "system") return ComputerDesktopIcon;
    return isDarkMode ? MoonIcon : SunIcon;
  };

  const CurrentIcon = getCurrentIcon();

  // 단순 토글 모드
  if (!showDropdown) {
    return (
      <button
        onClick={toggleDarkMode}
        className={`group relative inline-flex items-center justify-center rounded-xl transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 ${
          sizeClasses[size]
        } ${
          isDarkMode
            ? "bg-gray-800 text-yellow-400 hover:bg-gray-700 shadow-lg shadow-gray-900/25"
            : "bg-white text-gray-600 hover:bg-gray-50 shadow-lg shadow-gray-200/50 border border-gray-200"
        } ${className}`}
        title={`${isDarkMode ? "라이트" : "다크"} 모드로 전환`}
      >
        {/* 배경 그라데이션 효과 */}
        <div
          className={`absolute inset-0 rounded-xl transition-opacity duration-300 ${
            isDarkMode
              ? "bg-gradient-to-br from-gray-700 to-gray-900 opacity-80"
              : "bg-gradient-to-br from-blue-50 to-indigo-50 opacity-60"
          }`}
        />

        {/* 달 아이콘 */}
        <div
          className={`relative transition-all duration-500 ${
            isDarkMode
              ? "rotate-0 scale-100 opacity-100"
              : "rotate-90 scale-0 opacity-0"
          } absolute`}
        >
          <MoonIcon className={`${iconSizeClasses[size]} drop-shadow-sm`} />
        </div>

        {/* 태양 아이콘 */}
        <div
          className={`relative transition-all duration-500 ${
            isDarkMode
              ? "rotate-90 scale-0 opacity-0"
              : "rotate-0 scale-100 opacity-100"
          } absolute`}
        >
          <SunIcon className={`${iconSizeClasses[size]} drop-shadow-sm`} />
        </div>

        {/* 호버 효과 */}
        <div className="absolute inset-0 rounded-xl bg-white/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
      </button>
    );
  }

  // 드롭다운 모드
  return (
    <div className="relative">
      <button
        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
        className={`group relative inline-flex items-center justify-center gap-2 rounded-xl transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800 ${
          isDarkMode
            ? "bg-gray-800 text-yellow-400 hover:bg-gray-700 shadow-lg shadow-gray-900/25"
            : "bg-white text-gray-600 hover:bg-gray-50 shadow-lg shadow-gray-200/50 border border-gray-200"
        } px-3 py-2 ${className}`}
        title="테마 설정"
      >
        <CurrentIcon className={iconSizeClasses[size]} />
        <ChevronDownIcon
          className={`h-4 w-4 transition-transform duration-200 ${
            isDropdownOpen ? "rotate-180" : ""
          }`}
        />
      </button>

      {/* 드롭다운 메뉴 */}
      {isDropdownOpen && (
        <>
          {/* 오버레이 */}
          <div
            className="fixed inset-0 z-10"
            onClick={() => setIsDropdownOpen(false)}
          />

          {/* 드롭다운 내용 */}
          <div className="absolute right-0 mt-2 w-56 bg-white dark:bg-gray-800 rounded-xl shadow-xl border border-gray-200 dark:border-gray-700 z-20 py-2">
            {themeOptions.map((option) => {
              const IconComponent = option.icon;
              const isSelected = themeMode === option.id;

              return (
                <button
                  key={option.id}
                  onClick={() => {
                    setTheme(option.id);
                    setIsDropdownOpen(false);
                  }}
                  className={`w-full flex items-center gap-3 px-4 py-3 text-left transition-colors duration-200 ${
                    isSelected
                      ? "bg-blue-50 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400"
                      : "text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  }`}
                >
                  <IconComponent className="h-5 w-5 flex-shrink-0" />
                  <div className="flex-1">
                    <div className="font-medium">{option.label}</div>
                    <div className="text-xs text-gray-500 dark:text-gray-400">
                      {option.description}
                    </div>
                  </div>
                  {isSelected && (
                    <div className="w-2 h-2 bg-blue-500 rounded-full" />
                  )}
                </button>
              );
            })}
          </div>
        </>
      )}
    </div>
  );
};

export default DarkModeToggle;
