import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import {
  PlayIcon,
  StopIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  EyeIcon,
  ChevronDownIcon,
  ChevronRightIcon,
  ClockIcon,
  CheckCircleIcon,
  ExclamationCircleIcon,
  ArrowPathIcon,
} from "@heroicons/react/24/outline";
import {
  orchestrationAPI,
  ORCHESTRATION_STEPS,
  PROMPT_CARD_CATEGORIES,
  promptCardAPI,
} from "../services/api";

const OrchestrationPlayground = ({ projectId }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [articleContent, setArticleContent] = useState("");
  const [orchestrationConfig, setOrchestrationConfig] = useState({
    useAllSteps: true,
    enabledSteps: [],
    maxRetries: 3,
    temperature: 0.7,
  });
  const [currentExecution, setCurrentExecution] = useState(null);
  const [executionStatus, setExecutionStatus] = useState(null);
  const [results, setResults] = useState([]);
  const [isExecuting, setIsExecuting] = useState(false);
  const [expandedSteps, setExpandedSteps] = useState(new Set());

  // 프롬프트 카드 로드
  useEffect(() => {
    loadPromptCards();
  }, [projectId]);

  const loadPromptCards = async () => {
    try {
      const response = await promptCardAPI.getPromptCards(projectId, true, true);
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("프롬프트 카드 로드 실패:", error);
      toast.error("프롬프트 카드를 불러오는데 실패했습니다");
    }
  };

  // 오케스트레이션 실행
  const executeOrchestration = async () => {
    if (!articleContent.trim()) {
      toast.error("기사 내용을 입력해주세요");
      return;
    }

    try {
      setIsExecuting(true);
      setResults([]);

      const response = await orchestrationAPI.executeOrchestration(
        projectId,
        articleContent,
        orchestrationConfig
      );

      setCurrentExecution(response.executionId);
      setExecutionStatus('RUNNING');

      // 상태 폴링 시작
      pollExecutionStatus(response.executionId);

      toast.success("오케스트레이션이 시작되었습니다!");
    } catch (error) {
      console.error("오케스트레이션 실행 실패:", error);
      toast.error("오케스트레이션 실행에 실패했습니다");
      setIsExecuting(false);
    }
  };

  // 실행 상태 폴링
  const pollExecutionStatus = async (executionId) => {
    const poll = async () => {
      try {
        const status = await orchestrationAPI.getOrchestrationStatus(projectId, executionId);
        setExecutionStatus(status.status);

        if (status.status === 'COMPLETED') {
          const result = await orchestrationAPI.getOrchestrationResult(projectId, executionId);
          setResults(result.steps || []);
          setIsExecuting(false);
          toast.success("오케스트레이션이 완료되었습니다!");
        } else if (status.status === 'FAILED') {
          setIsExecuting(false);
          toast.error("오케스트레이션이 실패했습니다");
        } else if (status.status === 'RUNNING') {
          // 3초 후 다시 폴링
          setTimeout(poll, 3000);
        }
      } catch (error) {
        console.error("상태 조회 실패:", error);
        setIsExecuting(false);
      }
    };

    poll();
  };

  // 단계 확장/축소 토글
  const toggleStepExpansion = (stepId) => {
    const newExpanded = new Set(expandedSteps);
    if (newExpanded.has(stepId)) {
      newExpanded.delete(stepId);
    } else {
      newExpanded.add(stepId);
    }
    setExpandedSteps(newExpanded);
  };

  // 카테고리별 프롬프트 카드 그룹화
  const groupedPromptCards = promptCards.reduce((groups, card) => {
    const category = card.category || 'other';
    if (!groups[category]) groups[category] = [];
    groups[category].push(card);
    return groups;
  }, {});

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* 헤더 */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            🎭 AI 제목 생성 놀이터
          </h1>
          <p className="text-gray-600">
            프롬프트 오케스트레이션을 통한 지능형 제목 생성 시스템
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* 왼쪽: 입력 패널 */}
          <div className="lg:col-span-1 space-y-6">
            {/* 기사 내용 입력 */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-center mb-4">
                <DocumentTextIcon className="h-5 w-5 text-blue-600 mr-2" />
                <h3 className="text-lg font-semibold text-gray-900">기사 내용</h3>
              </div>
              <textarea
                value={articleContent}
                onChange={(e) => setArticleContent(e.target.value)}
                placeholder="제목을 생성할 기사 내용을 입력하세요..."
                rows={12}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
              />
              <div className="flex items-center justify-between mt-4">
                <span className="text-sm text-gray-500">
                  {articleContent.length}자
                </span>
                <button
                  onClick={executeOrchestration}
                  disabled={isExecuting || !articleContent.trim()}
                  className={`flex items-center px-4 py-2 rounded-lg font-medium transition-colors ${
                    isExecuting || !articleContent.trim()
                      ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                      : "bg-blue-600 text-white hover:bg-blue-700"
                  }`}
                >
                  {isExecuting ? (
                    <>
                      <ArrowPathIcon className="h-4 w-4 mr-2 animate-spin" />
                      실행 중...
                    </>
                  ) : (
                    <>
                      <PlayIcon className="h-4 w-4 mr-2" />
                      생성 시작
                    </>
                  )}
                </button>
              </div>
            </div>

            {/* 오케스트레이션 설정 */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-center mb-4">
                <Cog6ToothIcon className="h-5 w-5 text-purple-600 mr-2" />
                <h3 className="text-lg font-semibold text-gray-900">실행 설정</h3>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    창의성 (Temperature): {orchestrationConfig.temperature}
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={orchestrationConfig.temperature}
                    onChange={(e) =>
                      setOrchestrationConfig({
                        ...orchestrationConfig,
                        temperature: parseFloat(e.target.value),
                      })
                    }
                    className="w-full"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    최대 재시도 횟수
                  </label>
                  <select
                    value={orchestrationConfig.maxRetries}
                    onChange={(e) =>
                      setOrchestrationConfig({
                        ...orchestrationConfig,
                        maxRetries: parseInt(e.target.value),
                      })
                    }
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  >
                    <option value={1}>1회</option>
                    <option value={2}>2회</option>
                    <option value={3}>3회</option>
                    <option value={5}>5회</option>
                  </select>
                </div>
              </div>
            </div>

            {/* 프롬프트 카드 요약 */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-center mb-4">
                <SparklesIcon className="h-5 w-5 text-green-600 mr-2" />
                <h3 className="text-lg font-semibold text-gray-900">프롬프트 카드</h3>
              </div>

              <div className="space-y-3">
                {PROMPT_CARD_CATEGORIES.map((category) => {
                  const cards = groupedPromptCards[category.id] || [];
                  const activeCards = cards.filter(card => card.enabled !== false);

                  return (
                    <div key={category.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center">
                        <span className="text-lg mr-2">{category.icon}</span>
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {category.name}
                          </div>
                          <div className="text-xs text-gray-500">
                            {activeCards.length}/{cards.length} 활성
                          </div>
                        </div>
                      </div>
                      <div className={`w-2 h-2 rounded-full ${
                        activeCards.length > 0 ? 'bg-green-500' : 'bg-gray-300'
                      }`} />
                    </div>
                  );
                })}
              </div>
            </div>
          </div>

          {/* 오른쪽: 실행 결과 패널 */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center">
                  <EyeIcon className="h-5 w-5 text-orange-600 mr-2" />
                  <h3 className="text-lg font-semibold text-gray-900">실행 결과</h3>
                </div>
                {executionStatus && (
                  <div className={`flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                    executionStatus === 'RUNNING' 
                      ? 'bg-blue-100 text-blue-800'
                      : executionStatus === 'COMPLETED'
                      ? 'bg-green-100 text-green-800'
                      : executionStatus === 'FAILED'
                      ? 'bg-red-100 text-red-800'
                      : 'bg-gray-100 text-gray-800'
                  }`}>
                    {executionStatus === 'RUNNING' && <ClockIcon className="h-4 w-4 mr-1" />}
                    {executionStatus === 'COMPLETED' && <CheckCircleIcon className="h-4 w-4 mr-1" />}
                    {executionStatus === 'FAILED' && <ExclamationCircleIcon className="h-4 w-4 mr-1" />}
                    {executionStatus === 'RUNNING' ? '실행 중' : 
                     executionStatus === 'COMPLETED' ? '완료' :
                     executionStatus === 'FAILED' ? '실패' : executionStatus}
                  </div>
                )}
              </div>

              {/* 실행 단계별 결과 */}
              {results.length > 0 ? (
                <div className="space-y-4">
                  {results.map((step, index) => {
                    const stepInfo = Object.values(ORCHESTRATION_STEPS).find(s => s.id === step.stepType);
                    const isExpanded = expandedSteps.has(step.stepType);

                    return (
                      <div key={step.stepType} className="border border-gray-200 rounded-lg">
                        <button
                          onClick={() => toggleStepExpansion(step.stepType)}
                          className="w-full flex items-center justify-between p-4 text-left hover:bg-gray-50"
                        >
                          <div className="flex items-center">
                            <span className="text-lg mr-3">
                              {PROMPT_CARD_CATEGORIES.find(cat => cat.orchestration_step === step.stepType)?.icon || '🔧'}
                            </span>
                            <div>
                              <div className="font-medium text-gray-900">
                                {stepInfo?.name || step.stepType}
                              </div>
                              <div className="text-sm text-gray-500">
                                소요시간: {step.duration}ms | 토큰: {step.tokensUsed || 0}
                              </div>
                            </div>
                          </div>
                          <div className="flex items-center">
                            <div className={`w-2 h-2 rounded-full mr-3 ${
                              step.status === 'completed' ? 'bg-green-500' :
                              step.status === 'failed' ? 'bg-red-500' : 'bg-yellow-500'
                            }`} />
                            {isExpanded ? (
                              <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                            ) : (
                              <ChevronRightIcon className="h-5 w-5 text-gray-400" />
                            )}
                          </div>
                        </button>

                        {isExpanded && (
                          <div className="px-4 pb-4 border-t border-gray-100">
                            <div className="mt-3 space-y-3">
                              {step.input && (
                                <div>
                                  <div className="text-sm font-medium text-gray-700 mb-1">입력</div>
                                  <div className="text-sm text-gray-600 bg-gray-50 p-3 rounded border">
                                    {step.input}
                                  </div>
                                </div>
                              )}

                              {step.output && (
                                <div>
                                  <div className="text-sm font-medium text-gray-700 mb-1">출력</div>
                                  <div className="text-sm text-gray-900 bg-blue-50 p-3 rounded border border-blue-200">
                                    {step.output}
                                  </div>
                                </div>
                              )}

                              {step.error && (
                                <div>
                                  <div className="text-sm font-medium text-red-700 mb-1">오류</div>
                                  <div className="text-sm text-red-600 bg-red-50 p-3 rounded border border-red-200">
                                    {step.error}
                                  </div>
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div className="text-center py-12">
                  <SparklesIcon className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">
                    아직 실행 결과가 없습니다
                  </h3>
                  <p className="text-gray-500">
                    기사 내용을 입력하고 "생성 시작" 버튼을 클릭해보세요
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrchestrationPlayground;
