import axios from "axios";

// API 기본 설정
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  "https://vph0fu827a.execute-api.us-east-1.amazonaws.com/prod";

// Axios 인스턴스
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
  timeout: 30000, // 30초 타임아웃 (AI 처리 시간 고려)
});

// 요청 인터셉터
api.interceptors.request.use((config) => {
  console.log("API 요청:", config.method?.toUpperCase(), config.url);
  return config;
});

// 응답 인터셉터
api.interceptors.response.use(
  (response) => {
    console.log("API 응답:", response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error("API 오류:", error.response?.status, error.message);
    return Promise.reject(error);
  }
);

// =============================================================================
// 1. 프로젝트 API
// =============================================================================

export const projectAPI = {
  getProjects: async () => {
    const response = await api.get("/projects");
    return response.data;
  },

  getProject: async (projectId) => {
    const response = await api.get(`/projects/${projectId}`);
    return response.data;
  },

  createProject: async (projectData) => {
    const response = await api.post("/projects", projectData);
    return response.data;
  },

  updateProject: async (projectId, projectData) => {
    const response = await api.put(`/projects/${projectId}`, projectData);
    return response.data;
  },

  deleteProject: async (projectId) => {
    const response = await api.delete(`/projects/${projectId}`);
    return response.data;
  },

  getUploadUrl: async (projectId, fileName) => {
    const response = await api.get(`/projects/${projectId}/upload-url`, {
      params: { fileName },
    });
    return response.data;
  },
};

