export const filterProjects = (projects, filters) => {
  let filtered = [...projects];

  // 카테고리 필터
  if (filters.category && filters.category !== "all") {
    filtered = filtered.filter(
      (project) => project.category === filters.category
    );
  }

  // 검색어 필터
  if (filters.searchQuery) {
    const query = filters.searchQuery.toLowerCase();
    filtered = filtered.filter(
      (project) =>
        project.name?.toLowerCase().includes(query) ||
        project.description?.toLowerCase().includes(query) ||
        project.tags?.some((tag) => tag.toLowerCase().includes(query))
    );
  }

  // 정렬
  switch (filters.sortBy) {
    case "created":
      filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      break;
    case "updated":
      filtered.sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt));
      break;
    case "name":
      filtered.sort((a, b) => a.name?.localeCompare(b.name));
      break;
    default:
      break;
  }

  return filtered;
};

export const formatTokenCount = (count) => {
  if (count >= 1000) {
    return `${(count / 1000).toFixed(1)}K`;
  }
  return count.toString();
};

export const formatFileSize = (bytes) => {
  if (bytes >= 1024) {
    return `${(bytes / 1024).toFixed(1)}KB`;
  }
  return `${bytes}B`;
};

export const calculatePromptStats = (promptCards) => {
  if (!promptCards || promptCards.length === 0) {
    return {
      totalCards: 0,
      totalTokens: 0,
      averageTokens: 0,
      categories: [],
    };
  }

  const totalTokens = promptCards.reduce((sum, card) => {
    return sum + (card.tokenCount || 0);
  }, 0);

  const categories = [...new Set(promptCards.map((card) => card.category))];

  return {
    totalCards: promptCards.length,
    totalTokens,
    averageTokens: Math.round(totalTokens / promptCards.length),
    categories,
  };
};
