import React from "react";
import ReactMarkdown from "react-markdown";
import {
  ExclamationTriangleIcon,
} from "@heroicons/react/24/outline";

// 타임스탬프 포맷팅 유틸리티 함수
const formatTimestamp = (timestamp) => {
  if (!timestamp) return "";

  try {
    // 이미 Date 객체인 경우
    if (timestamp instanceof Date) {
      return timestamp.toLocaleTimeString('ko-KR', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    }

    // 문자열인 경우 Date 객체로 변환
    if (typeof timestamp === "string") {
      const date = new Date(timestamp);
      return isNaN(date.getTime()) ? "" : date.toLocaleTimeString('ko-KR', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    }

    // 숫자인 경우 (타임스탬프)
    if (typeof timestamp === "number") {
      return new Date(timestamp).toLocaleTimeString('ko-KR', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    }

    return "";
  } catch (error) {
    console.warn("타임스탬프 포맷 오류:", error);
    return "";
  }
};

const SimpleChatMessage = ({ message, onCopyMessage, onCopyTitle, copiedMessage }) => {
  // 사용자가 대화창에서 전송한 메시지면 사용자 메시지!
  const isUser = message.role === "user" || message.type === "user";
  const isError = message.role === "error";
  const isLoading = message.isLoading || false;

  // 복사 기능
  const handleCopyClick = async () => {
    try {
      await navigator.clipboard.writeText(message.content);
      // 팝업 알림 없이 복사만 실행
    } catch (err) {
      console.error('복사 실패:', err);
    }
  };


  return (
    <div className={`flex ${isUser ? "justify-end" : "justify-start"} mb-3`}>
      {isUser ? (
        // 사용자 메시지 - 우측 말풍선
        <div className="max-w-[70%]">
          <div 
            className="bg-[#5E89FF] text-white rounded-2xl px-4 py-2 shadow-md"
          >
            <div className="text-[15px] font-normal leading-[1.5] whitespace-pre-wrap break-words">
              {message.content}
            </div>
            {/* 타임스탬프 - 말풍선 내부 하단 */}
            {message.timestamp && (
              <div className="text-[12px] text-white/70 mt-1 text-right">
                {formatTimestamp(message.timestamp)}
              </div>
            )}
          </div>
        </div>
      ) : (
        // AI 메시지 - 좌측 플랫형 답변
        <div className="w-full max-w-[75%]">
          <div 
            className="bg-transparent dark:bg-transparent" 
            aria-live="polite"
          >
            {isError ? (
              <div className="text-red-600 dark:text-red-400">
                <div className="flex items-center mb-2">
                  <ExclamationTriangleIcon className="h-4 w-4 mr-2" />
                  <span className="text-[14px] font-semibold">오류가 발생했습니다</span>
                </div>
                <div className="text-[15px] font-normal leading-[1.5]">{message.content}</div>
              </div>
            ) : isLoading ? (
              // 예쁜 로딩 애니메이션 - 제미나이 스타일
              <div className="flex items-start gap-3">
                {/* AI 아이콘 */}
                <div className="flex-shrink-0 w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center shadow-md">
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 2L13.09 8.26L22 9L13.09 9.74L12 16L10.91 9.74L2 9L10.91 8.26L12 2Z" />
                  </svg>
                </div>

                {/* 로딩 애니메이션 */}
                <div className="flex-1">
                  <div className="flex items-center gap-1 mb-2">
                    <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                    <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                    <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                  </div>
                  <span className="text-sm text-gray-600 dark:text-gray-400">AI가 답변을 생성하고 있습니다...</span>
                </div>
              </div>
            ) : (
              // 일반 AI 응답 - 제미나이 스타일 로고 포함
              <div className="flex items-start gap-3">
                {/* AI 아이콘 */}
                <div className="flex-shrink-0 w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center shadow-md mt-1">
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 2L13.09 8.26L22 9L13.09 9.74L12 16L10.91 9.74L2 9L10.91 8.26L12 2Z" />
                  </svg>
                </div>

                {/* 답변 내용 */}
                <div className="flex-1 text-[#202124] dark:text-[#E5E7EB] prose prose-sm max-w-none">
                  <div className="text-[15px] font-normal leading-[1.6] whitespace-pre-wrap [&>p]:mb-3 [&>ul]:mb-3 [&>ol]:mb-3 [&>h1]:text-[18px] [&>h1]:font-semibold [&>h1]:mb-3 [&>h2]:text-[16px] [&>h2]:font-semibold [&>h2]:mb-2 [&>h3]:text-[15px] [&>h3]:font-medium [&>h3]:mb-2">
                    <ReactMarkdown>{message.content}</ReactMarkdown>
                  </div>
                </div>
              </div>
            )}
            {/* 타임스탬프 - 아이콘 위치에 맞춘 여백 */}
            {message.timestamp && (
              <div className="text-[12px] text-gray-500 dark:text-gray-400 mt-1 ml-11">
                {formatTimestamp(message.timestamp)}
              </div>
            )}
          </div>

          {/* AI 메시지 하단 액션 버튼들 - GPT 스타일 */}
          {!isError && !isLoading && (
            <div className="flex items-center gap-1 mt-2 ml-11">
              {/* 복사 버튼 */}
              <button
                onClick={handleCopyClick}
                className="p-1.5 text-gray-400 hover:text-gray-600 dark:text-gray-500 dark:hover:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-800 rounded transition-all duration-200"
                title="복사"
              >
                <svg 
                  className="w-4 h-4" 
                  fill="none" 
                  viewBox="0 0 24 24" 
                  stroke="currentColor"
                  strokeWidth="2"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
                </svg>
              </button>

              {/* 좋아요 버튼 */}
              <button
