import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  ArrowLeftIcon,
  StarIcon,
  ShareIcon,
  EllipsisHorizontalIcon,
  PlusIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  promptCardAPI,
} from "../services/api";
import ClaudeStyleWorkspace from "./workspace/ClaudeStyleWorkspace";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [promptCards, setPromptCards] = useState([]);

  const loadProject = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const projectData = await projectAPI.getProject(projectId);
      setProject(projectData);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  const loadPromptCards = useCallback(async () => {
    try {
      const response = await promptCardAPI.getPromptCards(
        projectId,
        false,
        true
      );
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("프롬프트 카드 로딩 실패:", error);
      setPromptCards([]);
    }
  }, [projectId]);

  useEffect(() => {
    loadProject();
    loadPromptCards();
  }, [loadProject, loadPromptCards]);


  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">프로젝트 로딩 중...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={() => navigate(-1)}
            className="text-blue-600 hover:text-blue-800 hover:underline"
          >
            돌아가기
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {/* 깔끔한 헤더 */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate(-1)}
              className="flex items-center text-gray-600 hover:text-gray-900 px-2 py-1 rounded hover:bg-gray-100 transition-colors"
            >
              <ArrowLeftIcon className="h-4 w-4 mr-1" />
              <span className="text-sm">모든 프로젝트</span>
            </button>
            <div className="border-l border-gray-300 h-4"></div>
            <h1 className="text-xl font-semibold text-gray-900">
              {project?.name}
            </h1>
          </div>
        </div>
      </div>

      {/* 통합 워크스페이스 */}
      <div className="h-[calc(100vh-73px)]">
        <ClaudeStyleWorkspace
          projectId={projectId}
          projectName={project?.name}
          onCardsChanged={loadPromptCards}
        />
      </div>
    </div>
  );
};


export default ProjectDetail;
